//	DJC:	Created 14th March 2000.package uk.ac.bham.cs.djc.ui;import uk.ac.bham.cs.djc.*;import javax.swing.JPanel;import java.awt.GridLayout;import javax.swing.JButton;import java.awt.event.ActionListener;import uk.ac.bham.cs.djc.time.TimesOfInterest;import uk.ac.bham.cs.djc.time.Interest;/**	A panel in which textual descriptions events can be displayed * 	 * 	<a href="EventPanel.java">Source code</a> * 	@author David Chatting (djc@cs.bham.ac.uk) *	@version Created 14th March 2000 */public class EventPanel extends JPanel{    /**	Create a new EventPanel     * 	@param	events the events to display      * 	@param	ResultsPanel a reference to active ResultsPanel     */    public EventPanel(TimesOfInterest events,ResultsPanel resultsPanel) {	super(new GridLayout(events.numOfTemporalMediaBlocks(),1));	//Set layout		Interest currentInterest=null;	//for(each event):	for(int n=0;n<events.numOfTemporalMediaBlocks();n++) {	    //Get the associated interest object:	    currentInterest=events.getInterestN(n);	    	    //Label a new button with the description of this event:	    String description=currentInterest.getDescription();	    if(description==null) description=new String("Show");	    JButton currentButton=new JButton(description);	    //Add a listener for this button:	    currentButton.addActionListener(new EventButtonListener(currentInterest,resultsPanel));	    //Add the button:	    add(currentButton);	    	    currentButton=null;	}    }        public void getFocusedButton() {	int n=0;	boolean foundFocus=false;		if(getComponentCount()>0) {	    do {		if(((JButton)getComponent(n)).hasFocus()) {			foundFocus=true;		}		else n++;	    } while(n<getComponentCount() && !foundFocus);	}    }}