package uk.ac.bham.cs.djc.ui;import javax.swing.JPanel;import javax.swing.JButton;import java.awt.GridLayout;import uk.ac.bham.cs.djc.*;/**	Panel in which parameter descriptions are displayed. * 	 * 	<a href="ParameterDescriptionPanel.java">Source code</a> * 	@author David Chatting (djc@cs.bham.ac.uk) *	@version Created 20th March 2000 */public class ParameterDescriptionPanel extends JPanel {    /**	Create a new ParameterDescriptionPanel object      */    public ParameterDescriptionPanel() {	super();    }        /**	Add a parameter to this panel to display      *	@param	newParameter new parameter to add     */    public void addParameter(Parameter newParameter) {    	add(new ParameterDescription(newParameter));    }}